// Enum for Message Types
export interface mockData {
  id: number;
  image: string;
  title: string;
  description: string;
  stats: {
    list: number;
    views: number;
    loves: number;
  };
}
export interface RandomData {
  id: number;
  title: string;
  category: string;
  author: string;
  img: string;
}

export interface FixedData {
  title: string;
  category: string;
  author: string;
  img: string;
}

export interface _MockData {
  id: number;
  type: ContentReviewMessage;
  data_random: RandomData[];
  data_fixed: FixedData[];
}

export interface GridData {
  id: number;
  image: string;
  title: string;
  writer: string;
  status: {
    love: string; // Change to number
    see: string; // Keep as string if '57K' includes non-numeric characters
    keep: string; // Change to number
  };
  tags?: string[];
  story?: string;
}

